package com.hzpd.adapter;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import java.util.ArrayList;import java.util.List;public class AutoAdapter extends ArrayAdapter {    List<String> data = new ArrayList<>();    public AutoAdapter(Context context, int resource, int textViewResourceId, List<String> list) {        super(context, resource, textViewResourceId, list);        this.data.addAll(list);    }    public void update() {        clear();        data.clear();        try {            for (String key : history) {                if (!data.contains(key)) {                    data.add(key);                    add(key);                }            }            for (String key : search) {                if (!data.contains(key)) {                    data.add(key);                    add(key);                }            }            notifyDataSetChanged();        } catch (Exception e) {        }    }    private List<String> history = new ArrayList<>();    private List<String> search = new ArrayList<>();    public void setHistory(List<String> history) {        this.history = history;        update();    }    public void setSearch(List<String> search) {        this.search = search;        update();    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        return super.getView(position, convertView, parent);    }}