package com.hzpd.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.hzpd.hflt.R;import com.hzpd.modle.TagBean;import com.hzpd.utils.Log;import java.util.ArrayList;import java.util.List;public class ClassifyItemAdapter extends RecyclerView.Adapter {    private LayoutInflater mInflater;    private List<Integer> mDatas;    List<TagBean> list = null;    public ClassifyItemAdapter(Context context) {        this.mInflater = LayoutInflater.from(context);        list = new ArrayList<TagBean>();    }    public static class ViewHolder extends RecyclerView.ViewHolder {        public ViewHolder(View arg0) {            super(arg0);        }        ImageView mImg;        TextView mTxt;    }    public void appendData(List<TagBean> data) {        if (data == null) {            return;        }        Log.i("appendData", "appendData   data" + data);        list.addAll(data);        Log.i("appendData", "appendData" + list.toString());        notifyDataSetChanged();    }    @Override    public int getItemCount() {        if (list.size()>0)            Log.i("list.size()","list.size()"+list.size());        else            Log.i("list.size()","list.size()=0");        return list.size();    }    /**     * 创建ViewHolder     */    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup viewGroup, int position) {        View view = mInflater.inflate(R.layout.classify_item_layout,                viewGroup, false);        ViewHolder viewHolder = new ViewHolder(view);        viewHolder.mImg = (ImageView) view                .findViewById(R.id.id_index_gallery_item_image);        viewHolder.mTxt= (TextView) view.findViewById(R.id.id_index_gallery_item_text);        return viewHolder;    }    /**     * 设置值     */    public void onBindViewHolder(ViewHolder holder, int position) {        Log.i("","list.size()"+list.size());        Log.i("onBindViewHolder","onBindViewHolder"+list.get(position).toString());        TagBean tagBean=list.get(position);        if (tagBean.getName()!=null)            holder.mTxt.setText(tagBean.getName());    }}