package com.hzpd.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.hzpd.hflt.R;import com.hzpd.modle.TagBean;import com.hzpd.utils.DisplayOptionFactory;import com.hzpd.utils.SPUtil;import java.util.ArrayList;import java.util.List;public class ClassifyItemAdapter extends RecyclerView.Adapter {    private LayoutInflater mInflater;    private View.OnClickListener onClickListener;    List<TagBean> list = null;    public ClassifyItemAdapter(Context context, View.OnClickListener onClickListener) {        this.mInflater = LayoutInflater.from(context);        list = new ArrayList<>();        this.onClickListener = onClickListener;    }    public class ItemViewHolder extends RecyclerView.ViewHolder {        public ItemViewHolder(View v) {            super(v);        }        public TagBean tagBean;        public View classify_item;        public ImageView mImg;        public TextView mTxt;    }    public void appendData(List<TagBean> data) {        if (data == null) {            return;        }        list.addAll(data);        notifyDataSetChanged();    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = mInflater.inflate(R.layout.classify_item_layout,                parent, false);        ItemViewHolder viewHolder = new ItemViewHolder(view);        viewHolder.classify_item = view.findViewById(R.id.classify_item);        viewHolder.mImg = (ImageView) view                .findViewById(R.id.id_index_gallery_item_image);        viewHolder.mTxt = (TextView) view.findViewById(R.id.id_index_gallery_item_text);        return viewHolder;    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        final ItemViewHolder viewHolder = (ItemViewHolder) holder;        final TagBean tagBean = list.get(position);        if (tagBean.getName() != null) {            viewHolder.mTxt.setText(tagBean.getName());        }        if (tagBean.getIcon() != null) {            SPUtil.displayImage(tagBean.getIcon(), viewHolder.mImg                    , DisplayOptionFactory.getOption(DisplayOptionFactory.OptionTp.Personal_center_News));        }        viewHolder.tagBean = tagBean;        if (last == null && position == 1) {            viewHolder.classify_item.setSelected(true);            last = viewHolder;        }        viewHolder.classify_item.setTag(viewHolder);        viewHolder.mImg.setLayoutParams(SPUtil.LARGE);        viewHolder.classify_item.setOnClickListener(onClickListener);    }    public ItemViewHolder last;    @Override    public int getItemCount() {        if (list.size() > 0) {            return list.size();        } else {            return 0;        }    }}